mapper.select:
SELECT u.uid uid, u.nickname nickname, u.avatar avatar, u.exp exp, a.aid aid, a.title title, (select count(id) from article_comment where article_comment.aid = a.aid) comment_num, a.article_type article_type, a.create_time create_time, a.update_time update_time FROM article AS a JOIN user AS u ON a.uid = u.uid
and a.top = #{topStatus}
and a.article_type = #{articleType}
and (select count(id) from article_comment where article_comment.aid = a.aid) = #{commentNum} and a.article_type != 2
and a.open_procedure = 0
ORDER BY a.aid desc LIMIT #{start},#{rows};
ORDER BY a.update_time desc ,a.aid desc LIMIT #{start},#{rows};

select count(aid) from article


        and top = #{topStatus}


        and (select count(id) from article_comment where article_comment.aid = article.aid) = #{commentNum}


        and article_type = #{articleType}

    and open_procedure = 0

select count(id) from collect_blog


        and aid = #{aid}

    and uid = #{uid}
